var React = require('react'),
    Link = require('react-router').Link,
    moment = require('moment');

module.exports = React.createClass({
    render: function () {
        var date = moment.unix(this.props.date);

        return (
            <li className='ball'>
                <Link to='forecasts' params={{ day: this.props.day.toLowerCase() }}>
                    { date.format('MMM') }
                    <span className='day'>
                        { date.format('DDD') }
                    </span>
                </Link>
            </li>
        );
    }
});
