var React = require('react');

module.exports = React.createClass({
    render: function () {
        var hour = 0,
            options = [];

        while (hour < 24) {
            // note that React will complain about flagging the selected option this way
            // but this is the only way you can get it to work with server-side rendering
            options.push(
                <option
                    key={ hour }
                    selected={ hour === this.props.hour } value={ hour }>
                        { (hour <= 12) ? (hour == 0 ? 12 : hour) : (hour - 12)  }:00
                        &nbsp;
                        { (hour < 12) ? 'AM' : 'PM' }
                </option>
            );
            hour++;
        }

        return (
            <select className='select-hour' name={ this.props.name } value={ this.props.hour }>
                { options }
            </select>
        );
    }
});
