var React = require('react'),
    Link = require('react-router').Link;

module.exports = React.createClass({
    render: function () {
        // just an example of using a map to compose views
        var links = ['Yesterday', 'Today', 'Tomorrow'].map(function (day) {
            return (
                <li>
                    <Link to='forecasts' params={{ day: day.toLowerCase() }}>
                        { day }
                    </Link>
                </li>
            );
        });

        return (
            <nav className='weather-nav'>
                <ul>
                    { links }
                </ul>
            </nav>
        );
    }
});
