var q = require('q'),
    request = require('superagent');

module.exports = {
    get: function (day) {
        var def = q.defer();

        request
            .get('/api/forecasts/' + day)
            .set('Accept', 'application/json')
            .end(function (data) {
                def.resolve(data.body);
            });

        return def.promise;
    }
};
