var React = require('react'),
    Router = require('react-router'),
    Route = Router.Route,
    DefaultRoute = Router.DefaultRoute,
    App = require('app/components/app'),
    Home = require('app/components/home'),
    Forecast = require('app/components/forecast');

module.exports = (
    <Route handler={ App }>
        <DefaultRoute handler={ Home } />
        <Route name='forecasts' path='forecasts' handler={ Forecast } />
    </Route>
);
