var React = require('react'),
    Router = require('react-router'),
    Route = Router.Route,
    DefaultRoute = Router.DefaultRoute,
    App = require('app/components/app'),
    ForecastPage = require('app/components/forecasts/page'),
    AboutPage = require('app/components/about/page');

module.exports = (
    <Route handler={ App }>
        <DefaultRoute handler={ ForecastPage } />
        <Route name='forecasts' path='forecasts/:day' handler={ ForecastPage } />
        <Route name='about' handler={ AboutPage } />
    </Route>
);
